changeSet.sql:
update case_field
set field_type_id = (select id from field_type where reference = 'DateTime')
where reference in ('[CREATED_DATE]', '[LAST_MODIFIED_DATE]', '[LAST_STATE_MODIFIED_DATE]');

changeSet.rollback:
update case_field
set field_type_id = (select id from field_type where reference = 'Date')
where reference in ('[CREATED_DATE]', '[LAST_MODIFIED_DATE]', '[LAST_STATE_MODIFIED_DATE]');
